insert  into `y_casus`(`ID`,`TITLE`,`CONTENT`,`INPUTDATE`,`admin_id`,`ord`,`count`) values ('84368264-37a4-46bd-b7b6-2765c862554f','蝙蝠在线考试系统欢迎您的使用','蝙蝠在线考试系统欢迎您的使用，请到后台&ldquo;系统设置&rdquo;-&ldquo;授权信息&rdquo;中输入授权信息。<br />\r\n<br />\r\n初始系统内置测试数据，若要清理测试数据，请到后台&ldquo;试题管理&rdquo;-&ldquo;导入导出&rdquo;中清理试题数据，到&ldquo;部门&amp;用户&rdquo;-&ldquo;导入导出&rdquo;中清理测试用户数据。','2014-06-01 02:20:17',NULL,1,2),('d550a386-5bd9-409e-b408-77c28668deec','考生须知（测试数据）','一、考试开始前15分钟考生凭学生证和有效身份证件（身份证等）进入规定考场对号入座，并将有效证件放在考桌左上角，以便监考人员查验。考试开始指令发出后，考生才能开始答卷。<br />\r\n二、考生进入考场必须关闭各种通讯工具。<br />\r\n参加闭卷考试考生在入场时除携带必要的文具外，不准携带其它物品(如:书籍、资料、笔记本和自备草稿纸以及具有收录、储存、记忆功能的电子工具等)。已携带入场的应按要求指定位置存放。<br />\r\n参加非闭卷考试的考生除携带必要的文具外，可携带该考试科目规定允许的相关资料。<br />\r\n三、考试开始30分钟后，考生停止进入考场（听力考试开始至结束，考生不得进出考场）。开考30分钟后考生方可交卷离开考场。考生交卷后应立即退场，不得在考场附近逗留、交谈，不得再返回考场续考。<br />\r\n四、考生领到试卷后，应清点试卷是否齐全，检查试卷有无缺损、错印等情况，若发现试卷差错应举手向监考人员报告。<br />\r\n五、考生答卷前，在试卷密封线内填写指定内容（如姓名、学号等）。凡漏写姓名、学生证号、座位号或字迹模糊无法辨认，以及在试卷密封线外填写学生证号、姓名或作其他标记的试卷一律按零分处理。<br />\r\n六、考生答卷时只允许用黑、蓝色钢笔或圆珠笔书写。特殊要求的科目（如使用答题卡）按具体要求执行。<br />\r\n七、考生不得询问试题题意，若发现试题字迹模糊或试题有误，可举手向监考人员询问，不准询问其他考生。<br />\r\n八、考生必须服从监考人员的监督管理。不准交头接耳，左顾右盼，传递物品，打手势，做暗号；不准擅自借用其他考生文具；不准偷看、抄袭他人答卷或允许他人抄袭自己的答卷；严禁夹带；严禁换卷、替考，以及其他违纪、舞弊行为。<br />\r\n九、在考试期间原则上不允许上厕所，若遇特殊情况，须由工作人员陪同出入考场。<br />\r\n十、考试结束指令发出后，考生立即停止答卷，将答卷（答题卡）反扣在桌面上，并按监考人员要求退离考场。严禁将试卷、答卷（答题卡）和考场统一发放的草稿纸带出考场。<br />\r\n十一、留考考生必须服从考试工作人员和监考人员的安排，不得与其他考生或场外人员接触。<br />\r\n&nbsp;<br />\r\n关于临时学生证办理的说明<br />\r\n各位考生： &nbsp;<br />\r\n&nbsp; &nbsp; 为了严肃考试纪律及学生证的使用，教务处研究决定，每位学生在校期间只能申请办理一次用于考试的临时学生证。申请过的学生，在校期间不得再次申请。 &nbsp; &nbsp;请各位考生携带双证参加考试。如有丢失，请及时办理补办手续。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 教务处 &nbsp; &nbsp;<br />\r\n<br />\r\n&nbsp;&nbsp;<br />\r\n&nbsp; &nbsp;&nbsp;','2015-04-30 23:43:11',NULL,2,1);

